// Generate purple coins for each kingdom with individual guides
export const smoPurpleCoins: Collectible[] = kingdomCoinData.flatMap(({ kingdomId, kingdomName, coinDesign, locations }) => {
  let coinIndex = 0;
  return locations.flatMap(({ count, guide, imageUrl }) =>
    Array.from({ length: count }, () => {
      coinIndex++;
      return {
        id: `${kingdomId}-coin-${String(coinIndex).padStart(3, '0')}`,
        name: `${kingdomName} Kingdom Purple Coin ${coinIndex}`,
        type: 'purple_coin' as const,
        kingdom: kingdomId,
        order: coinIndex,
        description: coinDesign,
        guide: guide,
        ...(imageUrl && { imageUrl }),
      };
    })
  );
});

// Verified total: 1000 purple coins
// 50 coins: Cap, Cascade, Lake, Lost, Snow, Moon (6 kingdoms = 300)
// 100 coins: Sand, Wooded, Metro, Seaside, Luncheon, Bowser's, Mushroom (7 kingdoms = 700)
// 0 coins: Cloud, Ruined, Dark Side, Darker Side (4 kingdoms)
